    const cartItems = (
        <ul className={classes['cart-items']}>
            {cartCtx.items.map((item) => (
                <CartItem key={item.id} name={item.name} amount={item.amount} price={item.price} onRemove />
            ))}
        </ul>
    );

    return (
        <Modal onClose={props.onClose}>
